import { Component, HostListener, OnDestroy } from '@angular/core';
import { NavigationEnd, Router } from '@angular/router';
import { filter, Subscription } from 'rxjs';

interface NavLink {
  label: string;
  fragment?: string;
  route?: string;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnDestroy {
  isMobileMenuOpen = false;
  isScrolled = false;
  private navigationSub?: Subscription;

  navLinks: NavLink[] = [
    { label: 'Início', fragment: 'home', route: '/' },
    { label: 'Arquitetura', fragment: 'arquitetura', route: '/' },
    { label: 'Operações', fragment: 'operacoes', route: '/' },
    { label: 'Tutoriais', fragment: 'tutoriais', route: '/' },
    { label: 'Links', fragment: 'links', route: '/' }
  ];

  constructor(private readonly router: Router) {
    this.navigationSub = this.router.events
      .pipe(filter(event => event instanceof NavigationEnd))
      .subscribe(() => {
        this.isMobileMenuOpen = false;
      });
  }

  toggleMobileMenu(): void {
    this.isMobileMenuOpen = !this.isMobileMenuOpen;
  }

  closeMobileMenu(): void {
    this.isMobileMenuOpen = false;
  }

  @HostListener('window:scroll')
  onWindowScroll(): void {
    this.isScrolled = window.scrollY > 10;
  }

  ngOnDestroy(): void {
    this.navigationSub?.unsubscribe();
  }
}

<header class="header" [class.header--scrolled]="isScrolled">
  <nav class="nav container">
    <div class="header-brand">
      <a routerLink="/" class="logo" (click)="closeMobileMenu()">
        <i class="fas fa-project-diagram"></i>
        DataFlow
      </a>
    </div>

    <ul class="nav-links" [class.mobile-active]="isMobileMenuOpen">
      <li *ngFor="let link of navLinks">
        <a
          [routerLink]="link.route"
          [fragment]="link.fragment"
          (click)="closeMobileMenu()"
          routerLinkActive="active"
          [routerLinkActiveOptions]="{ exact: link.fragment !== undefined }"
        >
          {{ link.label }}
        </a>
      </li>
      <li>
        <a routerLink="/docs/operations/instalacao-docker" routerLinkActive="active" (click)="closeMobileMenu()">Docs</a>
      </li>
    </ul>

    <button class="mobile-menu-toggle" aria-label="Menu" (click)="toggleMobileMenu()">
      <i class="fas" [class.fa-times]="isMobileMenuOpen" [class.fa-bars]="!isMobileMenuOpen"></i>
    </button>
  </nav>
</header>

<router-outlet></router-outlet>

<footer class="footer">
  <div class="container">
    <div class="footer-content">
      <div class="footer-brand">
        <h3>DataFlow</h3>
        <p>Plataforma de Ingestão e Processamento de Dados</p>
      </div>
      <div class="footer-links">
        <a routerLink="/" fragment="home">Início</a>
        <a routerLink="/" fragment="arquitetura">Arquitetura</a>
        <a routerLink="/" fragment="operacoes">Operações</a>
        <a routerLink="/" fragment="tutoriais">Tutoriais</a>
        <a routerLink="/" fragment="links">Links</a>
      </div>
    </div>
    <div class="footer-bottom">
      <p>&copy; 2024 DataFlow - Portal de Documentação. Todos os direitos reservados.</p>
    </div>
  </div>
</footer>
